CREATE database pokemon_game;

-- DROP TABLE IF EXISTS pokemon_game.pokemons;

CREATE TABLE `pokemon_game`.`pokemons` (
  `id` INT NOT NULL,
  `name` VARCHAR(45) NULL,
  `description` VARCHAR(500) NULL,
  `available` TINYINT NULL,
  PRIMARY KEY (`id`),
  UNIQUE INDEX `id_UNIQUE` (`id` ASC) VISIBLE);


Select * from pokemon_game.pokemons;